import $ from 'jquery';
import gsap from 'gsap';
import { TimelineLite, CSSPlugin } from 'gsap/all';
const plugins = [ CSSPlugin ]; 

gsap.registerPlugin(plugins);

export function dissolveFlashIn (SVGGroup, duration, onFinish) {
    console.log("Dissolving in...");
    SVGGroup.forEach(element => {
        gsap.to($(element), duration, {css: {opacity: $(element).data('og_opacity')},
                                        delay: 2,
                                        onComplete: onFinish,
                                        onCompleteParams: [element, 0.5, null]});
    })
}

export function dissolveFadeFromWhite(element, duration, onFinish) {
    console.log("Fadeing from white...");
    gsap.to($(element), duration, {css: {fill: $(element).data('og_fill'),
                                         stroke: $(element).data('og_stroke')},
                                    onComplete: onFinish,
                                    onCompleteParams: [duration, null]});
}

export function setElementTransform(element, rotation, translation, pivot) {
    gsap.set(element, {
        rotation: rotation,
        transformOrigin: pivot,
        x: translation.x,
        y: translation.y
    })
}
