import { HashRouter } from 'react-router-dom';
import React, {Component} from 'react';
import '../style.css';
import $ from 'jquery';

// Holds a component for a project that will be clicked on to show the actual project page
export default class ProjectPreviewElement extends Component {
    constructor (props) {
        super(props);
        this.state = {
            name: this.props.name,
            desc: this.props.desc,
            page: this.props.page,
            img: this.props.img,
        }
    }

    render() {
        let subtitleClass = "ProjectSubtitleXR";
        switch (this.props.pageID) {
            case 1:
                subtitleClass = "ProjectSubtitleUX";
                break;

            case 2:
                subtitleClass = "ProjectSubtitleSWE";
                break;

            default:
                break;
        }

        // TODO: Pass SVG Elements as props via https://stackoverflow.com/questions/39652686/pass-react-component-as-props
        // TODO: OnClick switch to target site
        return(<div className="ProjectElementWrapper">
                <h2 className="ProjectWhiteText">{this.state.name}</h2>
                <p className={subtitleClass}>{this.state.desc}</p>
                <button className={subtitleClass} onClick={() => {this.props.viewDetails(this.state)}}>View Project</button>
            </div>);
    }
}
