import React from 'react';
import "../style.css";
import $ from 'jquery';

import gsap from "gsap";
import { TweenMax } from 'gsap/all';

class NavigationBar extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            title: this.props.title,
            logo: "Phipson",
            onChange: this.props.onChange
        }

        this.changeTitleText = this.changeTitleText.bind(this);
        this.changeLogoButton = this.changeLogoButton.bind(this);
    }


    componentDidMount() {
        this.props.onRef(this);
    }

    componentWillUnmount() {
        this.props.onRef(null);
    }

    changeLogoButton(newButtonText, newOnChange) {
        TweenMax.to($(".PortfolioLogo"), {css: {opacity: 0}, duration: 0.2,
                    onComplete: () => {
                        this.setState(() => ({
                            logo: newButtonText,
                            onChange: newOnChange
                        }), () => {
                            TweenMax.to($(".PortfolioLogo"), {css: {opacity: 1}, duration: 0.2});
                        })
                    }})
    }

    changeTitleText(newText) {
        TweenMax.to($(".PortfolioTitle"), {css: {opacity: 0}, duration: 0.2,
                    onComplete: () => {
                        this.setState(() => ({
                            title: newText,
                        }), () => {
                            TweenMax.to($(".PortfolioTitle"), {css: {opacity: 1}, duration: 0.2});
                        })
                    }})
    }

    // Uses the onStateChange function passed from App.js to change pages
    render() { 
      return (
            <div className="PortfolioMenuWrapper">
                <div className="PortfolioLogo" onClick={this.props.onChange}>
                    {this.state.logo}
                </div>
                <div className="PortfolioTitle">
                    {this.state.title}
                </div>
                <div className="PortfolioHamburger">

                </div>
            </div>
        );
    }
}

export default NavigationBar;
