import './style.css';
import PhipsonSVG from './PhipsonSVG';
import { HashRouter } from 'react-router-dom';
import React from 'react';

import LandingPage from './components/Landing';
import NavigationBar from './components/NavigationBar';

class App extends React.Component{
  constructor (props) {
    super(props);
    this.state = {
      pageID: 0, // FOR LANDING PAGE
      siteState: 0, // FOR NAVIGATION
      headerText: "",  // FOR NAVIGATION DISPLAY (SYSTEM STATUS)
    }

    this.changePageIDState = this.changePageIDState.bind(this);
  }

  changePageIDState(newPageID) {
    console.log("Changing pageID to: ", newPageID);
    this.setState(() => ({
      pageID: newPageID%3
    }), () => {
      if (this.state.siteState === 0) {
        this.child.child.swapTimeline(this.state.pageID);
      }
    });
  }

  render () {
    let siteMap = {
      0: <LandingPage pageID={this.state.pageID} onChange={this.changePageIDState}/>,
    };

    return (
      <HashRouter basename='/'>
        <div className="App">
          <NavigationBar title={this.state.headerText}/>
          {siteMap[this.state.siteState]}
        </div>
      </HashRouter>
    );
  }
}

export default App;
