import { HashRouter } from 'react-router-dom';
import React, {Component} from 'react';
import '../style.css';
import $ from 'jquery';
import { TimelineMax, TweenMax } from 'gsap/all';

export default class SideCarousel extends Component {
    constructor(props) {
        super(props);
        this.handleCircleHoverEnter = this.handleCircleHoverEnter.bind(this);
        this.handleCircleHoverExit = this.handleCircleHoverExit.bind(this);
        this.handleCarouselHoverEnter = this.handleCarouselHoverEnter.bind(this);
        this.handleCarouselHoverExit = this.handleCarouselHoverExit.bind(this);

        this.hoverCarouselTimeline = new TimelineMax({paused: true, 
                                                     onComplete: function() {
                                                        TweenMax.set($(".SideCarouselHoverText"), {css: {display: "none"}});
                                                    },
                                                     onStart: function() {
                                                        TweenMax.set($(".SideCarouselHoverText"), {css: {display: "block"}});
                                                     }});
    }

    componentDidMount() {

    }

    // TODO: Work on hiding and showing text when hovering the carousel (vs hovering the individual circles)
    // TODO: Work on changing the top scroll location onClick
    // TODO: Work on basic dissolve and fade animation based on scrollTrigger
    handleCircleHoverEnter(e) {
        TweenMax.to($(e.target).find("circle"), {css: {fill: "white"}, duration: 0.2});
        TweenMax.to($(e.target).siblings("p"), {css: {opacity: 1}, duration: 0.2});
    }

    handleCircleHoverExit(e) {
        TweenMax.to($(e.target).find("circle"), {css: {fill: "none"}, duration: 0.2});
        TweenMax.to($(e.target).siblings("p"), {css: {opacity: 0.5}, duration: 0.2});
    }

    handleCarouselHoverEnter(e) {
        TweenMax.set($(".SideCarouselHoverText"), {css: {display: "block"}});
        TweenMax.to($(".SideCarouselHoverText"), {css: {opacity: 0.5, marginRight: "-=10px"},
                                                    duration: 0.2,
                                                    stagger: {
                                                        each: 0.08,
                                                        from: "start",
                                                        ease: "power1.inOut"
                                                    }});
    }

    handleCarouselHoverExit(e) {
        TweenMax.to($(".SideCarouselHoverText"), {css: {opacity: 0, marginRight: "+=10px"}, 
                                                    duration: 0.2,
                                                    stagger: {
                                                        each: 0.08,
                                                        from: "end",
                                                        ease: "power1.inOut"
                                                    }});
    }

    // Change element based on scrollTrigger (use button click to change scrollTop location, but keep all logic within scrollTrigger)
    render() {
        return(
            <div className="SideCarouselWrapper"
                onMouseEnter={(e) => this.handleCarouselHoverEnter(e)}
                onMouseLeave={(e) => this.handleCarouselHoverExit(e)}>
                {Object.values(this.props.projectList).map((element, i) => 
                    <div key={i} className="SideCarouselElement">
                        <p className="SideCarouselHoverText">{element.name}</p>
                        <svg className="SideCarouselHoverSVG" viewBox="0 0 16 16" xmlns="http://www.w3.org/2000/svg"
                            onMouseEnter={(e) => this.handleCircleHoverEnter(e)}
                            onMouseLeave={(e) => this.handleCircleHoverExit(e)}>
                            <circle r="3" transform="matrix(-1 0 0 1 8 8)" className="SideCarouselCircle"/>
                        </svg>
                    </div>
                )}
            </div>
        )
    }
}
