import { HashRouter } from 'react-router-dom';
import React, {Component} from 'react';
import '../style.css';
import $ from 'jquery';
import { TimelineMax, TweenMax } from 'gsap/all';

export default class SideCarousel extends Component {
    constructor(props) {
        super(props);
        this.handleHoverEnter = this.handleHoverEnter.bind(this);
        this.handleHoverExit = this.handleHoverExit.bind(this);
    }


    componentDidMount() {
        // $(".SideCarouselCircle").on("mouseenter", function() {
        //     TweenMax.to($(this), {css: {fill: "white"}, duration: 0.25});
        // })

        // $(".SideCarouselCircle").on("mouseleave", function() {
        //     TweenMax.to($(this), {css: {fill: "none"}, duration: 0.25});
        // })
    }

    handleHoverEnter(e) {
        TweenMax.to($(e.target).find("circle"), {css: {fill: "white"}, duration: 0.2});
        TweenMax.to($(e.target).siblings("p"), {opacity: 1});
    }

    handleHoverExit(e) {
        TweenMax.to($(e.target).find("circle"), {css: {fill: "none"}, duration: 0.2});
        TweenMax.to($(e.target).siblings("p"), {opacity: 0.5, duration: 0.2});
    }

    // Change element based on scrollTrigger (use button click to change scrollTop location, but keep all logic within scrollTrigger)
    render() {
        return(
            <div className="SideCarouselWrapper">
                {Object.values(this.props.projectList).map((element, i) => 
                    <div key={i} className="SideCarouselElement">
                        <p className="SideCarouselHoverText">{element.name}</p>
                        <svg viewBox="0 0 16 16" xmlns="http://www.w3.org/2000/svg"
                            onMouseEnter={(e) => this.handleHoverEnter(e)}
                            onMouseLeave={(e) => {TweenMax.to($(e.target).find("circle"), {css: {fill: "none"}, duration: 0.2})}}>
                            <circle r="3" transform="matrix(-1 0 0 1 8 8)" className="SideCarouselCircle"/>
                        </svg>
                    </div>
                )}
            </div>
        )
    }
}
